mapper.update:
UPDATE price SET
p_id = #{p_id}, s_name = #{s_name}, p_rent = #{p_rent}, p_price = #{p_price}, p_card = #{ p_card}, p_gift = #{p_gift}, p_text = #{p_text}, g_id = #{g_id}, id = #{id}
WHERE
P_ID = #{p_id}

UPDATE goods SET
g_name = #{g_name},
g_content = #{g_content},
m_id = #{m_id},
c_id = #{c_id}


        , g_img1 = #{g_img1}
        , g_rimg1 = #{g_rimg1}


        , g_img2 = #{g_img2}
        , g_rimg2 = #{g_rimg2}


        , g_img3 = #{g_img3}
        , g_rimg3 = #{g_rimg3}



        , g_img4 = #{g_img4}
        , g_rimg4 = #{g_rimg4}


WHERE g_id = #{g_id}

mapper.select:
SELECT GOODS.*, CATEGORY.C_NAME, MAKER.M_NAME FROM GOODS INNER JOIN CATEGORY ON GOODS.C_ID = CATEGORY.C_ID INNER JOIN MAKER ON GOODS.M_ID = MAKER.M_ID
AND CATEGORY.C_NAME = #{c_name}
AND MAKER.M_NAME IN
#{brand}
