mapper.select:
SELECT COUNT(*) as total FROM SUPPLIERBOARD s INNER JOIN `user` u2 ON s.ID = u2.ID WHERE u2.`role` = "supplier" AND (
s.sb_title LIKE '%${searchKeyword}%'
u2.name LIKE '%${searchKeyword}%'
s.sb_regdate LIKE '%${searchKeyword}%'
1 = 1
);

mapper.update:
UPDATE SUPPLIERBOARD 
SET sb_answer = '${sb_answer}'
WHERE sb_id = #{sb_id};

mapper.select:
SELECT *
FROM SUPPLIERBOARD
WHERE id = #{id}
ORDER BY sb_id DESC;
